function applyLanguage(lang) {
  const langData = translations[lang];
  if (!langData) return;

  document.querySelectorAll("[data-lang-key]").forEach((el) => {
    const key = el.getAttribute("data-lang-key");
    if (langData[key]) {
      el.innerHTML = langData[key];
    }
  });

  const guideLink = document.querySelector('.link-item[href*="github.com"]');
  if (guideLink) {
    guideLink.href =
      "vi" === lang
        ? "https://github.com/duckmartians/Auto-Flow/blob/main/README_vi.md"
        : "https://github.com/duckmartians/Auto-Flow/blob/main/README.md";
  }
}

document.addEventListener("DOMContentLoaded", () => {
  const selector = document.getElementById("languageSelector");

  chrome.storage.local.get(["language"], (saved) => {
    const lang = saved.language || "en";
    if (selector.querySelector(`option[value="${lang}"]`)) {
      selector.value = lang;
      applyLanguage(lang);
    } else {
      selector.value = "en";
      applyLanguage("en");
    }
  });

  selector.addEventListener("change", (e) => {
    const lang = e.target.value;
    applyLanguage(lang);
    chrome.storage.local.set({ language: lang });
  });

  // Collapsible sections
  document.querySelectorAll(".changelog-header.collapsible").forEach((header) => {
    header.addEventListener("click", () => {
      const targetId = header.getAttribute("data-toggle");
      const body = document.getElementById(targetId);
      if (!body) return;
      header.classList.toggle("collapsed");
      body.classList.toggle("collapsed");
    });
  });

  document.getElementById("btn-glabs").addEventListener("click", () => {
    window.open(
      "https://github.com/duckmartians/G-Labs-Automation",
      "_blank",
    );
  });

  document.getElementById("btn-classic").addEventListener("click", () => {
    window.location.href = "sidepanel.html";
  });
});
